var workData;
var WORK_ACTIVE = false;

$(document).ready(function() {
	resetAll();
	mobileSetup();
	loaderAnimation(() => {
		setTimeout(() => {
			loadPage();
		}, 1000);
	});
});

async function loadPage() {
	scrollHandler();
	await checkElement("._SS_wrapper");
	workData = await loadWorkData();
	await animatePageIn();
	navHandler();
	if (!isMobile()) {
		workListScroller();
		hoverTracker();
	}
	workClickListener();
}

function loaderAnimation(done) {
	// You gotta make it look like it's loading
	$("#content-container .load .loader").html("0%");
	setTimeout(() => {
		for(let i=0; i<18; i++) {
			setTimeout((index) => {
				$("#content-container .load .loader").html(index + "%");
			}, i*30, i);
		}
		setTimeout(() => {
			for(let i=17; i<64; i++) {
				setTimeout((index) => {
					$("#content-container .load .loader").html(index + "%");
				}, i*15, i);
			}
			setTimeout(() => {
				for(let i=65; i<=100; i++) {
					setTimeout((index) => {
						$("#content-container .load .loader").html(index + "%");
					}, i*15, i);
				}
				setTimeout(() => {
					done();
				}, 600)
			}, 1000)
		}, 1000);
	}, 1000);
}

function rafAsync() {
    return new Promise(resolve => {
        requestAnimationFrame(resolve);
    });
}

async function checkElement(selector) {
    let querySelector = null;
    while (querySelector === null) {
        await rafAsync();
        querySelector = document.querySelector(selector);
    }
    return querySelector;
}



