var workData;

$(document).ready(function() {
	resetAll();
	loadPage();
});

async function loadPage() {

	workData = await loadWorkData();
	mobileSetup();
	await animatePageIn();
	navHandler();
	easeScroll();
	if (!isMobile()) {
		workListScroller();
		workCanvas();
	}
	workClickListener();
}





/****************************************
 * 
 * ASYNC DATA LOADERS AND ELEMENT STATE SETTERS
 * 
 ****************************************/

function loadWorkData() {
	return new Promise((resolve, error) => {
		fetch("files/js/data.json")
			.then((data) => data.json())
			.then((data) => {
				let workContainer = "#content-container .work-list ul";

				for (i=0; i<data.length; i++) {
					$(workContainer).append(
						`<li class="list-item w-animate" data-id="${data[i].id}">
							<img draggable="false" data-lazysrc="files/imgs/work-back/${data[i].id}.jpg" alt="${data[i].title} Background Image">
							<p class="item-date w-animate">${data[i].date}</p>
							<div class="text-wrapper">
								<p class="item-summary w-animate">${data[i].summary}</p>
								<h1 class="item-title w-animate">${data[i].title}</h1>
							</div>
						</li>`);
				}
				resolve(data);
		});
	});
}


function resetAll() {
	$('.fixed-scroll').css("position", "fixed");
	$("#content-container .parallax-wrapper.home-back").css("transform", "translateY(50%)");
	$("#content-container .title-wrapper *, .nav-wrapper").css("transform", "translateY(200%)");
	$("#content-container .parallax-wrapper.home-back img").css("transform", "translateY(-40%)");
	$("#content-container .h-animate").css("transform", "translateY(-200%)");
	$("#content-container .parallax-wrapper.home-back, #content-container .h-animate, #content-container .title-wrapper *, .nav-wrapper").css("opacity", "0");
	$("#content-container .load .loader").css("opacity", "0");
	$("#content-container .load .loader").css("transform", "translateY(50px)");
	$(".back-parallax-wrapper").css("opacity", "0");
	anime({
		targets: "#content-container .load .loader",
		opacity:1,
		translateY: "0",
		easing: "cubicBezier(0.23, 1, 0.32, 1)",
		duration: 1000,
		delay: 1000
	});
}


function animatePageIn() {
	return new Promise((resolve, error) => {

		let promiseArray = [];
		
		$('img[data-lazysrc]').each( function(i, e) {
			let promise = new Promise((resolve, error) => {
				this.src = $(this).data('lazysrc');
				this.onload = () => {
					resolve(i);
				}
			});
			promiseArray.push(promise);
		});

		Promise.all(promiseArray).then(() => {
			anime({
				targets: "#content-container .load .loader",
				opacity:0,
				translateY: "-50px",
				duration: 1000,
				delay: 1000,
				easing: "cubicBezier(0.755, 0.05, 0.855, 0.06)",
				complete: function() {
					$("#content-container .load").remove();
					
					anime({
						targets: ".nav-wrapper, .back-parallax-wrapper",
						translateY: "0%",
						opacity:1,
						easing: "cubicBezier(0.165, 0.84, 0.44, 1)",
						duration: 1500,
						delay: 150
					});

					anime({
						targets: "#content-container .title-wrapper *",
						translateY: "0%",
						opacity: 1,
						easing: "cubicBezier(0.165, 0.84, 0.44, 1)",
						duration: 1500,
						delay: anime.stagger(300)
					})
					anime({
						targets: "#content-container .parallax-wrapper.home-back, #content-container .parallax-wrapper.home-back img",
						translateY: "0%",
						opacity:1,
						easing: "cubicBezier(0.165, 0.84, 0.44, 1)",
						duration: 1500,
						delay: 300
					});
					setTimeout(function() {
						anime({
							targets: "#content-container .h-animate",
							translateY: "0%",
							opacity:1,
							easing: "cubicBezier(0.165, 0.84, 0.44, 1)",
							duration: 1200,
							delay: anime.stagger(300),
							complete: function() {
								resolve();
							}
						});
					}, 400);
				}
			});
		});
	});
}






/****************************************
 * 
 * MOBILE SUPPORT
 * 
 ****************************************/


function isMobile() {
	return /Android|webOS|iPhone|iPad|iPod|BlackBerry|IEMobile|Opera Mini/i.test(navigator.userAgent);
}

function mobileSetup() {
	if (isMobile()) {
		$(".wave-filter, #content-container.work-click-area ul").each(function() {
			$(this).addClass("disabled");
		});
		$("#content-container .text-content .scroll-msg.subheader").text("Swipe Up");
		$("#work-canvas").remove();
		$("#content-container.work-click-area").css("overflow-x", "auto");
	}
}





/****************************************
 * 
 * NAVIGATION BUTTON AND SCROLL HANDLER 
 * 
 ****************************************/


function navHandler() {
	var locations = [
		{ 
			name: 'home',
			location: 0
		},
		{
			name: 'work',
			location: $("#content-container.work-click-area").offset().top
		},
		{	name: 'about',
			location: $("#content-container.about").offset().top
		}
	];
	$("body").data("position", locations[0].name);

	$(window).resize(() => {
		locations[1].location = $("#content-container.work-click-area").offset().top;
		locations[2].location = $("#content-container.about").offset().top;
	});

	let timeout;

	$('li.scroller span').click((e) => {
		mobileNav(true);
		let dataName = $(e.target).parent().data("name");
		let locationObj = locations.find(e => e['name'] === dataName);
		$("body").data("position", locationObj.name);
		window.scroll({
			top: locationObj.location,
			behavior: 'smooth'
		});
	});

	document.addEventListener('wheel', (e) => {
		e.preventDefault();
		if (!timeout) {
			let isDown = (e.deltaY < 0) ? true : false;
			scrollPage(isDown);

			timeout = setTimeout(() => {
				timeout = undefined;
			}, 1000)
		}
	}, { passive: false });

	function scrollPage(isDown) {
		let dataName = $("body").data("position");
		let index = locations.findIndex(e => e['name'] === dataName);
		let newPosition;

		if (!isDown) {
			newPosition = ((index+1) == locations.length) ? false : locations[index+1];
		} else {
			newPosition = (index == 0) ? false : locations[index-1];
		}

		if (newPosition) $("body").data("position", newPosition.name);
		
		locations[1].location = $("#content-container.work-click-area").offset().top;
		locations[2].location = $("#content-container.about").offset().top;

		window.scroll({
			top: newPosition.location,
			behavior: 'smooth'
		});
	}

	// Mobile navigation handler

	$(".m-nav-wrapper ul li").css("opacity", "0");
	$(".m-nav-wrapper ul li").css("transform", "translateY(5vh)");

	$(".nav-wrapper .hb-button").click(function() {
		active = $(this).hasClass("close");
		mobileNav(active);
	});

	function mobileNav(close) {
		if (close) {
			anime({
				targets: ".m-nav-wrapper img.background",
				opacity: 0,
				easing: "cubicBezier(0.165, 0.84, 0.44, 1)",
				duration: 700,
				complete: () => {
					anime({
						targets: ".m-nav-wrapper ul li",
						opacity: 0,
						translateY: "5vh",
						easing: "cubicBezier(0.165, 0.84, 0.44, 1)",
						duration: 1000,
					});
					$(".nav-wrapper .hb-button").removeClass("close");
					$(".m-nav-wrapper").removeClass("active");
				}
			});
		} else {
			$(".nav-wrapper .hb-button").addClass("close");
			$(".m-nav-wrapper").addClass("active");
			anime({
				targets: ".m-nav-wrapper ul li, .m-nav-wrapper img.background",
				opacity: 1,
				translateY: "0vh",
				easing: "cubicBezier(0.165, 0.84, 0.44, 1)",
				duration: 1000,
				delay: anime.stagger(150, {start: 500})
			});
		}
	}
                                                  
	let yDown = null; let xDown = null; let checkHorizontal;                                                        

	document.addEventListener('touchstart', (e) => {
		yDown = e.touches[0].clientY;
		xDown = e.touches[0].clientX;
		console.log(checkHorizontal);
	}, false);

	document.addEventListener('touchmove', (e) => {
		checkHorizontal = ($(e.target).parents(".work-list").length);
		if (!checkHorizontal) e.preventDefault();
		
		if (e.cancelable && !checkHorizontal) e.preventDefault();

		if (!yDown || !xDown) return;

		let yUp = e.touches[0].clientY;
		let xUp = e.touches[0].clientX;
		let yDiff = yDown - yUp;
		let xDiff = xDown - xUp;
		
		if ( Math.abs( xDiff ) <= Math.abs( yDiff ) ) {
			e.preventDefault();
			isDown = (yDiff > 0) ? false : true;
			scrollPage(isDown);
		} else {
			e.returnValue = true;
		}

		xDown = null;
		yDown = null;
	}, { passive: false, cancelable: true });
}





/****************************************
 * 
 * SLIDESHOW HANDLER 
 * WORK LIST SLIDESHOW CLICK & DRAG HANDLER
 * 
 ****************************************/

function workListScroller() {

	let isClick, endPosition, initialMousePosX, initialPos;
	let slider = $('#content-container .work-list');
	let maxSpeed = 5000;

	slider.css("transform", "translateX(0px)");
	calculateEndPos();

	$("#work-canvas").bind('mousedown', (e) => {
		initialMousePosX = e.clientX;
		isClick = true;
		setTimeout(() => {
			if (isClick) {
				slider.addClass("hold");
				initialPos = slider.css("transform").match(/-?[\d\.]+/g);
				initialPos = parseInt(initialPos[4]);
			}
		}, 150);
	});

	$("#work-canvas").bind('mouseleave', () => {
		isClick = false;
		slider.removeClass("hold");
	});

	$("#work-canvas").bind('mouseup', () => {
		isClick = false;
		setTimeout(() => {
			slider.removeClass("hold");
		}, 10);
	});

	$("#work-canvas").bind('mousemove', (e) => {
		if (!isClick) return;
		e.preventDefault();
		
		secondPosX = e.clientX;
		diff = secondPosX - initialMousePosX;
		calcPosition = initialPos - (maxSpeed * (diff / document.body.clientWidth));
		
		if (calcPosition > 0) calcPosition = 0;
		if (calcPosition <= (endPosition * -1)) calcPosition = endPosition*-1;
		
		slider.css("transform", `translateX(${calcPosition}px)`);
	});



	$(window).resize(() => {
		calculateEndPos();
	});

	function calculateEndPos() {
		let perView, perEntireView;
		let listItems = slider.children("ul").children("li");
		let sliderWidth = 0;

		$("#content-container .work-list ul li").each(function() {
			sliderWidth += $(this).outerWidth();
			perView = Math.floor(slider.outerWidth()/$(this).outerWidth());
		});

		perEntireView = sliderWidth / listItems.length;
		endPosition = perEntireView * (listItems.length - perView);
	}
}






/****************************************
 * 
 * WORK CANVAS 
 * CANVAS MOUSE TRACKING, HOVERING, AND CLICKING
 * 
 ****************************************/

function workCanvas() {

	let defaultRadius = 35;
	let radius = defaultRadius;
	let canvas = $('#work-canvas');;
	let context = canvas[0].getContext('2d');
	let isClick = false;
	let circleFill = "#3a4257";
	let slider = $('#content-container .work-list');

	let x = 0;
	let y = 0;

	function listeners() {
		canvas.mousemove(mouseMove);
		canvas.mousedown(mouseDown);
		canvas.mouseup(mouseUp);
		canvas.mouseleave(mouseUp);
		canvas.click((e) => {
			let isHover = isItemHover(".list-item", e)

			if (isHover && !isClick && !slider.hasClass("hold")) {
				openWorkItem(isHover);
			}
		});
	};

	function mouseMove(e) {
		var t = 0;
		var text = "DRAG";
		var position = getMousePosition(e);
		var checkHoverEl = isItemHover(".list-item", e);
		
		if (!isClick) {
			if (checkHoverEl && !isClick) {
				$("#work-canvas").css("cursor", "pointer");
				$(checkHoverEl).addClass("hover");
			} else {
				$("#work-canvas").css("cursor", "default");
			}

			$(".list-item").each(function() {
				if (this != checkHoverEl) {
					$(this).removeClass("hover");
				}
			});
		}

		function loop() {
			if (!isClick) {

				context.clearRect(0, 0, canvas.width(), canvas.height());

				x += easeInOutQuad(t) * (position.x - x);
				y += easeInOutQuad(t) * (position.y - y);
				
				context.beginPath();
				context.arc(x, y, radius, 0, 2*Math.PI, false);
				context.fillStyle = circleFill;
				context.fill();
				context.fillStyle = 'white';
				context.font = '14px Questrial';
				context.textAlign = 'center';
				context.textBaseline = 'middle';
    			context.fillText(text, x, y);
				
				if (t < 1) {
					t += 0.015;
					requestAnimationFrame(loop);
				}
			}
		}
		loop();
	};

	function isItemHover(elem, e) {
		let isHover, hoverElem;
		
		$(elem).each(function (i) {
			let elemTop = $(this).offset().top;
			let elemBottom = elemTop + $(this).outerHeight();
			let elemLeft = $(this).offset().left;
			let elemRight = elemLeft + $(this).outerWidth();

			if (!isHover) {
				isHover = (((e.pageY <= elemBottom) && (e.pageY >= elemTop)) && ((e.pageX >= elemLeft) && (e.pageX <= elemRight)));
				hoverElem = this;
			}
		});

		if (isHover) return hoverElem;
		return false;
	}
	
	function mouseDown() {
		isClick = true;
		var t = 0;

		function loop() {
			context.clearRect(0, 0, canvas.width(), canvas.height());

			drawArrow(x+(50 * easeInOutQuad(t)), y, false);
			drawArrow(x-(50 * easeInOutQuad(t)), y, true);

			context.beginPath();
			context.arc(x, y, (radius - (10 * easeInOutQuad(t))), 0, 2*Math.PI, false);
			context.fillStyle = circleFill;
			context.fill();

			if (t < 1) {
				t += 0.04;
				requestAnimationFrame(loop);
			}
		} loop();
	}

	function mouseUp(e) {
		
		var t = 0.72;
		isClick = false;
		mouseMove(e);

		function loop() {
			context.clearRect(0, 0, canvas.width(), canvas.height());
			context.beginPath();
			context.arc(x, y, (radius * easeInOutQuad(t)), 0, 2*Math.PI, false);
			context.fillStyle = circleFill;
			context.fill();

			if (t < 1) {
				t += 0.015;
				requestAnimationFrame(loop);
			}
		} loop();
	}

	function getMousePosition(e) {
		return {
			x: e.offsetX,
			y: e.offsetY
		}
	}

	function easeInOutQuad(t) {
		return t < .5 ? 2 * t * t : -1 + (4 - 2 * t) * t;
	}


	function drawArrow(x, y, inv){
		context.moveTo(x, y);
		
		if (inv) {
			context.lineTo(x + 8, y + 7);
			context.moveTo(x, y);
			context.lineTo(x + 8, y - 7);
		} else {
			context.lineTo(x - 8, y + 7);
			context.moveTo(x, y);
			context.lineTo(x - 8, y - 7);
		}

		context.strokeStyle = circleFill;
		context.lineWidth = 2;
		context.stroke();
	}
	
	$(window).resize(() => {
		document.querySelector("#work-canvas").width = $("#content-container.work-click-area").outerWidth();
		document.querySelector("#work-canvas").height = $("#content-container.work-click-area").outerHeight();
	});
	document.querySelector("#work-canvas").width = $("#content-container.work-click-area").outerWidth();
	document.querySelector("#work-canvas").height = $("#content-container.work-click-area").outerHeight();
	
	listeners();
}






/****************************************
 * 
 * Work Click Listeners & Handlers 
 * REDUNDANT CODE, NEEDS TO BE REAUDITED
 * 
 ****************************************/

function openWorkItem(e) {
	openItem(parseInt($(e).data('id'))-1)
};

function openItem(id) {
	$("#listing-container .item-date,#listing-container .item-title p,#listing-container .role-container ul, #listing-container .links").html("")
	$("#listing-container .close-container").css("display", "block")
	$("#listing-container").addClass("moving")
	$("#listing-container .item-date").text(workData[id].date)
	$("#listing-container .item-title p").text(workData[id].title)
	for (i=0; i<workData[id].roles.length; i++) {
		$("#listing-container .role-container ul").append(`<li class="role">${workData[id].roles[i]}</li>`)
	}
	for (i=0; i<workData[id].links.length; i++) {
		$("#listing-container .links").append(`<a class="link-btn ${workData[id].links[i].type}" href="${workData[id].links[i].link}" target="_blank"><span>${workData[id].links[i].text}</span></li>`)
	}

	anime({
		targets: ".fixed-scroll",
		marginLeft: "10%",
		easing: "cubicBezier(0.86, 0, 0.07, 1)",
		duration: 800
	});
	anime({
		targets: "#listing-container .content-wrapper",
		paddingLeft: "7vw",
		easing: "cubicBezier(0.23, 1, 0.32, 1)",
		duration: 1050
	});
	anime({
		targets: "#listing-container",
		width: "100vw",
		easing: "cubicBezier(0.86, 0, 0.07, 1)",
		duration: 700,
		complete: function() {
			$("#listing-container").removeClass("moving")
		}
	});
}

function workClickListener() {
	$("#listing-container .close-container").css("display", "none")

	$(".close-container").click(function(e) {
		closeItem()
	});

	$(".list-item").click(function(e) {
		openWorkItem(this);
	});

	function closeItem() {
		$("#listing-container").addClass("moving")
		anime({
			targets: ".fixed-scroll",
			marginLeft: "0",
			easing: "cubicBezier(0.86, 0, 0.07, 1)",
			duration: 800
		});
		anime({
			targets: "#listing-container .content-wrapper",
			paddingLeft: "0",
			easing: "cubicBezier(0.86, 0, 0.07, 1)",
			duration: 800,
			complete: function() {
				$("#listing-container .close-container").css("display", "none")
			}
		});
		anime({
			targets: "#listing-container",
			width: "0vw",
			easing: "cubicBezier(0.86, 0, 0.07, 1)",
			duration: 700
		});
	}
}





/****************************************
 * 
 * Scroll animation handlers
 * 
 ****************************************/

function easeScroll() {
	animateOnScroll();

	m.transitions.mntm_scroll(".fixed-scroll", [
		{element: ".parallax-wrapper, .list-item", offsetY: 6},
		{element: ".fixed-scroll", offsetY: -1},
		{element: ".back-parallax-wrapper", offsetY: 4}
	], 1000, "cubic-bezier(0.19, 1, 0.22, 1)");

	$("body").css("overflow-y", "hidden");
}

function animateOnScroll() {

	var animated = [];

	$(".list-item, .list-item .w-animate, .a-animate").css("opacity", "0");
	$(".list-item, .list-item .w-animate, .a-animate").css("transform", "translateY(50%)");

	function inView(elem) {
	  var docViewTop = $(window).scrollTop();
	  var docViewBottom = docViewTop + $(window).height();

	  var elemTop = $(elem).offset().top;
	  var elemBottom = elemTop + ($(elem).height()/2);

	  return ((elemBottom <= docViewBottom) && (elemTop >= docViewTop));
	}

	function animate(el, i) {
		if (!animated.includes(el)) {
			animated.push(el);
			anime({
				targets: el,
				opacity:1,
				translateY: "0",
				easing: "cubicBezier(0.165, 0.84, 0.44, 1)",
				duration: 1000,
				complete: function() {
					$(el).addClass("scroll-done");
				},
				delay: anime.stagger(200, {start: i*300})
			});
		}
	}

	$(window).scroll(function() {
		if (inView("#content-container.work-click-area")) {
			setTimeout(() => {
				$('.list-item').each(function (index) {
					animate(".list-item", index);
					setTimeout(() => {
						animate(".list-item:nth-child("+(index+1)+") .item-title.w-animate", index);
					}, 200);
					setTimeout(() => {
						animate(".list-item:nth-child("+(index+1)+") .item-summary.w-animate", index);
					}, 300);
					setTimeout(() => {
						animate(".list-item:nth-child("+(index+1)+") .item-date.w-animate", index);
					}, 400);
				});
			}, 1000);
		}
		 
		if ($("body").data("position") == "about") {
			setTimeout(() => {
				animate("#content-container.about .a-animate", 1);
				$("#content-container.about .background").css("opacity", "1");
			}, 300);
			$("body").css("background-color", "#10121c");
			$(".back-parallax-wrapper .back-title").addClass("dark");
			$("#nav-container, .nav-wrapper .hb-button").addClass("white");
		} else {
			$("body").css("background-color", "#F4F6F8");
			$("#content-container.about .background").css("opacity", "0");
			$(".back-parallax-wrapper .back-title").removeClass("dark");
			$("#nav-container, .nav-wrapper .hb-button").removeClass("white");
		}
	});
}
